function init(svg) {
    var nl = NumberLine({ svg:svg });
    nl.addDrawable(StaticText({ text:"Number Line" }));
    nl.addDrawable(YearsSinceFiller({ time:0, fill:"blue" }));
    nl.addDrawable(BlackLeft({ x:0 }));
    nl.addDrawable(Fractions({ minTextPixels: 12.5, maxTextPixels: 96 }));
    nl.activate(window);
}
